<script>
//  import Progress from './Progress';
 import CardMenu from "./CardMenu.vue";
  import {mapActions} from 'vuex'
export default {
  name: "NewCard",
  data(){
      return {
          isModalVisible: false,
          links: [{
            name: 'Edit',
            action: () => {
              this.toggleMenu()
             // this.$emit('toggleShareModal')
              //alert('You cant assign tasks now')
            },
            type: 'default'
          },
          {
            name: 'Delete',
            action: () => {
              this.toggleMenu()
              alert('completed')
            },
            type: 'default'
          }
         
        ]
      }
  },
  components: {
      CardMenu,
      // Progress
  },
  computed: {
    time(){
      if(this.todo.created_at === undefined){
        return this.todo.start_date
      } else {
       return this.todo.created_at.slice(0,10)
      }
    },
    description(){
      let value = ''
      if(typeof(this.todo.description) != "string"){
         value = this.todo.description.description.slice(0, 20);
      
      } else {
        value = this.todo.description.slice(0, 20);
       
      }
      return value
    }
  },
  methods: {
      ...mapActions({
        delete: 'todos/ADD_TRASH',
        archive: 'todos/ADD_ARCHIVE'
      }),
      
      ClickAway() {
      this.isModalVisible = false;
    },
    details() {
      this.$router.push({ name: "Details", params: { id: this.todo._id } });
    },
    toggleMenu() {
      this.isModalVisible = !this.isModalVisible;
    },
    
    closeModal() {
      this.isModalVisible = false;
    },
    alert() {
      alert("hi");
    },
    handleArchivedTodo() {
      this.$emit("archived", this.todo);
    }
  }, 
  
  props: {
      todo: {
        type: Object,
      },
    }
  
};
</script>

<style lang="scss" scoped>
.percent {
    transform: translateY(-15px)
}
.taskcard {
  background: #ffffff;
  // border: 1px solid #dadada;
  box-sizing: border-box;
  border-radius: .6em;
  max-width: 30em;
  box-shadow: 0px 0px 15px #e7e7e7de;
}
</style>
